    function RealizarConsulta() {

        if (document.getElementById("realizar").innerHTML != "") {

            //programaÃ§Ã£o antiga mas pode ser mantida pra checar se o user nÃ£o inputou nada
            inputs = document.getElementsByClassName("valueConsulta")
            p = ""
            for (i = 0; i < inputs.length; i++) {
                if (inputs[i].getAttribute("type") == "checkbox") {
                    p = p + inputs[i].checked + ","
                } else {
                    p = p + inputs[i].value.split(",").join("") + ","
                }
            }
            p = p.slice(0, -1)
            p = p.split("/").join("")

            if (p == "") { return "" }

            try {
                document.getElementById("realizar").style.backgroundImage = "url(https://i-find.org/static/svgs/Loading.svg)"
                document.getElementById("realizar").style.backgroundSize = "100% 100%"
